function [Fimg] = dec_int(img,n)% PRIMEIRO DIMINUIR A IMAGEM POR UM FATOR    % Recebe as dimensões da imagem nas variáveis.    [linha, coluna, formato] = size(img);  [X,Y,Z] = meshgrid(1:coluna,1:linha,1:formato);    % Divide os valores das dimensões por n.  nx = coluna/n;  ny = linha/n;    % Recebe um vetor com nx elementos espaçados entre 1 e coluna.  Nx = linspace(1,coluna,nx);     % Recebe um vetor com ny elementos espaçados entre 1 e linha.  Ny = linspace(1,linha,ny);    % Faz o mesmo com o formato.   Nz = linspace(1,formato,3);     % Interpolação para novo tamanho.  [Nx,Ny,Nz] = meshgrid(Nx,Ny,Nz);   Nimg = uint8(interp3(X,Y,Z,double(img),Nx,Ny,Nz));% AGORA O PROCESSO INVERSO     % Recebe as dimensões da imagem nas variáveis.  [linha1, coluna1, formato1] = size(Nimg);  [X1,Y1,Z1] = meshgrid(1:coluna1,1:linha1,1:formato1);    % Multiplica os valores das dimensões por n.  nx1 = coluna1*n;  ny1 = linha1*n;    % Recebe um vetor com nx elementos espaçados entre 1 e coluna.  Nx1 = linspace(1,coluna1,nx1);     % Recebe um vetor com ny elementos espaçados entre 1 e linha.  Ny1 = linspace(1,linha1,ny1);    % Faz o mesmo com o formato.   Nz1 = linspace(1,formato1,3);     % Interpolação para tamanho final.  [Nx1,Ny1,Nz1] = meshgrid(Nx1,Ny1,Nz1);   Fimg = uint8(interp3(X1,Y1,Z1,double(Nimg),Nx1,Ny1,Nz1));    % Display da imagem original.##  subplot(1, 3, 1);##  imshow(img);##  title('Imagem Original', 'FontSize', 15);  % Display da imagem diminuída.##  subplot(1, 3, 2);##  imshow(Nimg);##  title('Imagem Diminuída', 'FontSize', 15);  % Display da imagem final.##  subplot(1, 3, 3);##  imshow(Fimg);##  title('Imagem Final', 'FontSize', 15);  endfunction