function [Fimg] = edge_improv(img)% Utilizando o operador laplaciano  % Centro de máscara negativo, portanto será feita subtração.  H = [0 1 0; 1 -4 1; 0 1 0];%  H = [1 1 1; 1 -8 1; 1 1 1];  % Para fazer a subtração, nega-se o filtro.  H = -1*H;  imgf = imfilter(img,H);  if H(2,2) < 0      Fimg = img-imgf;  else          Fimg = img+imgf;  end  % Display da imagem original.##  subplot(1, 3, 1);##  imshow(img);##  title('Imagem Original', 'FontSize', 15);  % Display da imagem detalhes finos.##  subplot(1, 3, 2);##  imshow(imgf);##  title('Imagem para soma', 'FontSize', 15);  % Display da imagem final.##  subplot(1, 3, 3);##  imshow(g);##  title('Imagem Final', 'FontSize', 15);endfunction